import React from "react";

export default function InfoTooltip(state, onClose) {
  return (
    <section
      className={`popup popup_info ${
        state !== "" ? "popup_opened" : ""
      }`}
      onClick={onClose}
    >
      <div className="info popup__container_info">
        <img className="popup__img" src={card.link} alt={card.name} onClick={(e) => e.stopPropagation()}/>
        <figcaption className="popup__caption">{card.name}</figcaption>
        <button
          type="button"
          className="popup__close popup__close_photo"
          onClick={onClose}
        />
      </div>
    </section>
  );
}
