import { useState, useEffect } from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import api from "../utils/api";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import { Navigate, Route, Routes, useNavigate } from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import { autorisation, autentification, checkUser } from "../utils/auth";
import ProtectedRoute from "./ProtectedRoute";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [deleteCard, setDeleteCard] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmit, setIsSubmit] = useState(false);
  const [isLogged, setIsLogged] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    api
      .getAppInfo()
      .then(([dataCard, dataUser]) => {
        setCurrentUser(dataUser);
        setCards(dataCard);
      })
      .finally(() => setIsLoading(false))
      .catch((err) => console.log("Ошибка получения данных", err));
  }, []);
//
const [email, setEmail] = useState("")

  // useEffect(() => {
  //   if (localStorage.jwt)
  //   getUserData(localStorage.jwt)
  //   .then (res => console.log (res))
  //   setIsEmail(res.data.email)
  //   setIsLoggetIn(true)
  //   navigate("/")
    // aut(pass, email)
    // localStorage
    // .then
  //   .catch((err) => console.log("Ошибка получения данных", err));

  // }, [navigate])
  function handleRegistration(formValue) {
// const navigate = useNavigate();


    setIsSubmit(true);
    autentification(formValuepass, email)
      .then((res) => {
        // localStorage.setItem("jwt", res.token)
        // setIsLoggedIn(true)
        // navigate("/");
      })
      .finally(() => setIsSubmit(false))
      .catch((err) =>
        console.log("Ошибка обновления данных пользователя", err)
      );
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsDeleteCardPopupOpen(false);
    setSelectedCard({});
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleCardLike({ card }) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api
      .changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      })
      .catch((err) => console.log("Ошибка изменения лайка", err));
  }

  function handleDeletePopupOpen(id) {
    setDeleteCard(id);
    setIsDeleteCardPopupOpen(true);
  }

  function handleDeleteCard(e) {
    e.preventDefault();
    setIsSubmit(true);
    api
      .deleteCardItem(deleteCard)
      .then(() => {
        setCards((cards) => cards.filter((c) => c._id !== deleteCard));
        closeAllPopups();
      })
      .finally(() => setIsSubmit(false))
      .catch((err) => console.log("Ошибка удаления карточки", err));
  }

  function handleUpdateUser(userData) {
    setIsSubmit(true);
    api
      .setUserInfo(userData)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .finally(() => setIsSubmit(false))
      .catch((err) =>
        console.log("Ошибка обновления данных пользователя", err)
      );
  }

  function handleUpdateAvatar(userData, resetInputs) {
    setIsSubmit(true);
    api
      .setUserAvatar(userData)
      .then((res) => {
        setCurrentUser(res);
        resetInputs();
      })
      .finally(() => setIsSubmit(false))
      .catch((err) =>
        console.log("Ошибка обновления аватара пользователя", err)
      );
  }

  function handleAddPlaceSubmit(dataCard, resetInputs) {
    setIsSubmit(true);
    api
      .addNewCard(dataCard)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        resetInputs();
      })
      .finally(() => setIsSubmit(false))
      .catch((err) => console.log("Ошибка добавления новой карточки", err));
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="body">
        <div className="wrapper">
          <Header name={"sign-up"}/>
          <Routes>
            <Route
              path="/"
              element={
                <ProtectedRoute
                  handleEditAvatarClick={() => {
                    setIsEditAvatarPopupOpen(true);
                  }}
                  handleEditProfileClick={() => {
                    setIsEditProfilePopupOpen(true);
                  }}
                  handleAddPlaceClick={() => {
                    setIsAddPlacePopupOpen(true);
                  }}
                  handleDeleteCardClick={() => {
                    setIsDeleteCardPopupOpen(true);
                  }}
                  onCardClick={handleCardClick}
                  handleDeleteClick={handleDeletePopupOpen}
                  cards={cards}
                  onCardLike={handleCardLike}
                  isLoading={isLoading}
                  isLoggedIn={isLogged}
                />
              }
            />
            <Route path="/sign-up" element={<Register onRegistration={handleRegistration}/>} />
            <Route path="/sign-in" element={<Login />} />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>

          <Footer />

          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
            isSubmit={isSubmit}
          />

          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onAddPlace={handleAddPlaceSubmit}
            isSubmit={isSubmit}
          />

          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
            isSubmit={isSubmit}
          />

          <PopupWithForm
            title="Вы уверены?"
            name="delete-card"
            formName="deleteCard"
            buttonText={"Да"}
            isOpen={isDeleteCardPopupOpen}
            onSubmit={handleDeleteCard}
            onClose={closeAllPopups}
            isSubmit={isSubmit}
          />

          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
