import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function Login() {
const navigate = useNavigate();
const location = useLocation();
const fromPage = location.state?.from?.pathname || '/'

  return (
    <section className="login">
      <h2 className="login__title">Вход</h2>
      <form method="post" className="login__form" name="login">
        <input type="email" className="login__input" placeholder="Email" />
        <input type="password" className="login__input" placeholder="Пароль" />
        <input type="submit" className="login__submit" value={"Войти"} />
      </form>
    </section>
  );
}
