import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function Register() {
  const navigate = useNavigate();
  const location = useLocation();
  const fromPage = location.state?.from?.pathname || "/";
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("")

  function handleSubmit () {
    console.log(fromPage)
  }

  function handleEmailChange(e) {
    setEmail(e.target.value);
  }
  function handlePassChange(e) {
    setPass(e.target.value);
  }

  return (
    <section className="login">
      <h2 className="login__title">Регистрация</h2>
      <form method="post" className="login__form" name="register">
        <input type="email" className="login__input" placeholder="Email" value={email} onChange={handleEmailChange}/>
        <input type="password" className="login__input" placeholder="Пароль" value={pass} onChange={handlePassChange}/>
        <input
          type="submit"
          className="login__submit"
          value={"Зарегистрироваться"}
          onSubmit={handleSubmit}
        />
      </form>
      <span className="login__text">
        Уже зарегистрированы?{" "}
        <Link to="/sign-in" className="login__link">
          Войти
        </Link>
      </span>
    </section>
  );
}
