import React from "react";

export default function InfoTooltip (state) {
  return (
  <div className={`popup popup_infotooltip ${state !== "" ? "popup_opened" : ""}`}>
    <div className="popup__container popup__container_infotooltip"></div>
    {
      state === "" ? (
        <></>
      )
       : state === "ok" ? (
        <img src="../../images/error.svg" alt="Что-то пошло не так" className="infotooltip__image_error"/>
      ) :  (
        <img src="../../images/ok.svg" alt="Вы успешно зарегистрировались" className="infotooltip__image_ok"/>
      )
    }
    {
      state === "ok" ? (
        <span className="infotooltip__content_ok">Вы успешно зарегистрировались!</span>
      )  : (
        <span className="infotooltip__content_error">Что-то пошло не так! Попробуйте ещё раз.</span>
      )
    }
  </div>
  );
}
